DROP TABLE IF EXISTS `ev_bill_delivery_items*`;
CREATE TABLE `ev_bill_delivery_items*` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `delivery_id` varchar(20) NOT NULL COMMENT '发货单号 关联bill_delivery.id',
