  KEY `aftersales_id` (`aftersales_id`),
  KEY `type` (`type`),
  KEY `IX_USER` (`user_id`),
  KEY `IX_STORE` (`site_id`),
  KEY `IX_AREA_MALL` (`mall_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='退款单表';


DROP TABLE IF EXISTS `ev_bill_reship*`;
CREATE TABLE `ev_bill_reship*` (
  `reship_id` varchar(20) NOT NULL,
  `mall_id` int(10) unsigned NOT NULL DEFAULT '0',
  `site_id` int(10) unsigned NOT NULL DEFAULT '0',
  `order_id` varchar(20) NOT NULL COMMENT '订单ID 关联order.id',
  `aftersales_id` varchar(20) NOT NULL COMMENT '售后单id',
