  PRIMARY KEY (`id`),
  UNIQUE KEY `IX_MALL_USER` (`mall_id`,`buyer_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='分站买家积分余额';


DROP TABLE IF EXISTS `ev_buyer_points_log`;
CREATE TABLE `ev_buyer_points_log` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `mall_id` smallint(4) unsigned NOT NULL DEFAULT '0',
  `order_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '订单ID',
  `buyer_id` int(10) unsigned NOT NULL COMMENT '用户ID',
  `type` tinyint(1) NOT NULL DEFAULT '1' COMMENT '类型 1=返积分 2=使用积分',
  `num` int(10) NOT NULL DEFAULT '0' COMMENT '积分数量',
  `balance` bigint(20) NOT NULL DEFAULT '0' COMMENT '积分余额',
  `remarks` varchar(255) DEFAULT NULL COMMENT '备注',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `IX_MALL_BUYER` (`buyer_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='用户积分流水表';


DROP TABLE IF EXISTS `ev_buyer_wx_login`;
CREATE TABLE `ev_buyer_wx_login` (
  `wx_login_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `buyer_id` int(10) unsigned NOT NULL COMMENT '会员ID',
  `login_openid` varchar(50) NOT NULL COMMENT '用户openid',
  `login_appid` varchar(30) NOT NULL COMMENT '登录公众号',
  `login_unionid` varchar(30) NOT NULL DEFAULT '' COMMENT '登录时获得的UninoId',
  `login_nickname` varchar(50) NOT NULL DEFAULT '' COMMENT '登录的昵称',
  `login_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '用户登录时间',
  `is_subscribe` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '是否订阅',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`wx_login_id`),
  UNIQUE KEY `UK_BUYERWXLOGIN_openId` (`login_openid`),
  KEY `IX_BUYER_ID` (`buyer_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AVG_ROW_LENGTH=204 COMMENT='买家登录记录表';


DROP TABLE IF EXISTS `ev_cart*`;
CREATE TABLE `ev_cart*` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `mall_id` int(10) unsigned NOT NULL DEFAULT '0',
  `user_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '用户ID 关联user.id',
  `site_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '店铺ID',
  `product_id` int(10) unsigned DEFAULT '0' COMMENT '货品ID',
  `nums` smallint(5) unsigned DEFAULT '0' COMMENT '货品数量',
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`),
  KEY `IX_PRODUCT` (`product_id`),
  KEY `IX_AREA_MALL_SITE` (`site_id`),
  KEY `IX_AREA_MALL` (`mall_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='购物车表（每个用户最多加100条信息）';


DROP TABLE IF EXISTS `ev_data_express`;
CREATE TABLE `ev_data_express` (
  `express_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '物流公司ID',
  `e_name` varchar(50) NOT NULL COMMENT '物流公司名称',
  `e_code` varchar(50) NOT NULL COMMENT '物流公司编号',
  `e_letter` char(1) NOT NULL COMMENT '物流公司首字母',
  `e_url` varchar(100) NOT NULL COMMENT '物流公司URL',
  `e_state` tinyint(3) unsigned NOT NULL COMMENT '物流公司状态,0:不启用，1：启用',
  `code` varchar(10) DEFAULT NULL COMMENT '物流公司code',
  `created_at` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `updated_at` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`express_id`),
  KEY `INDEX_E_CODE` (`e_code`),
  KEY `INDEX_CODE` (`code`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AVG_ROW_LENGTH=348 COMMENT='物流公司模板表';


DROP TABLE IF EXISTS `ev_goods`;
CREATE TABLE `ev_goods` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '商品ID',
  `mall_id` int(10) unsigned NOT NULL DEFAULT '0',
  `site_id` int(10) unsigned NOT NULL DEFAULT '0',
  `page_recommend_num` int(10) NOT NULL DEFAULT '0' COMMENT '首页推荐指数(大于0的就是推荐的)',
  `bn` varchar(30) NOT NULL COMMENT '商品货号',
  `name` varchar(200) NOT NULL COMMENT '商品名称',
  `brief` varchar(255) DEFAULT NULL COMMENT '商品简介',
  `tiaolou` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商品的挑楼费(元/层 (搬运上楼费用))',
  `handing_fee` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商品退货搬运费',
  `price` decimal(10,2) NOT NULL COMMENT '商品价格',
  `price1` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '一级会员价格(不设置代表按照会员折扣来算)',
  `price2` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '二级会员价格(不设置代表按照会员折扣来算)',
  `price3` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '三级会员价格(不设置代表按照会员折扣来算)',
  `price4` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '四级会员价格(不设置代表按照会员折扣来算)',
  `price5` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '五级会员价格(不设置代表按照会员折扣来算)',
  `price6` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '六级会员价格(不设置代表按照会员折扣来算)',
  `image_id` char(32) DEFAULT NULL COMMENT '默认图片 图片id',
  `goods_cat_id` int(10) unsigned DEFAULT NULL COMMENT '商品分类ID 关联category.id',
  `goods_type_id` int(10) unsigned DEFAULT NULL COMMENT '商品类别ID 关联goods_type.id',
  `brand_id` int(10) unsigned DEFAULT NULL COMMENT '品牌ID 关联brand.id',
  `is_nomal_virtual` tinyint(1) unsigned DEFAULT '1' COMMENT '虚拟正常商品 1=正常 2=虚拟',
  `marketable` tinyint(1) unsigned DEFAULT '1' COMMENT '上架标志 1=上架 2=下架 3售罄（上架商品库存计算出来的）',
  `stock` int(8) DEFAULT '0' COMMENT '库存',
  `freeze_stock` int(8) DEFAULT '0' COMMENT '冻结库存',
  `weight` decimal(10,2) unsigned DEFAULT NULL COMMENT '重量',
  `unit` varchar(20) DEFAULT NULL COMMENT '商品单位',
  `intro` longtext COMMENT '商品详情',
  `spes_desc` text COMMENT '商品规格序列号存储',
  `params` text COMMENT '参数序列化',
  `trans_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '商品运费模板ID',
  `freight_price` decimal(6,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '商品固定运费',
  `is_store_freight` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '商品运费是否按店铺设定的运费规则，1是，0否',
  `comments_count` int(10) unsigned DEFAULT '0' COMMENT '评论次数',
  `view_count` int(10) unsigned DEFAULT '0' COMMENT '浏览次数',
  `buy_count` int(10) unsigned DEFAULT '0' COMMENT '购买次数',
  `uptime` bigint(12) unsigned DEFAULT NULL COMMENT '上架时间',
  `downtime` bigint(12) unsigned DEFAULT NULL COMMENT '下架时间',
  `sort` smallint(5) unsigned DEFAULT '100' COMMENT '商品排序 越小越靠前',
  `is_recommend` tinyint(1) unsigned DEFAULT '2' COMMENT '是否推荐，1是，2不是推荐',
  `is_hot` tinyint(1) unsigned DEFAULT '2' COMMENT '是否热门，1是，2否',
  `label_ids` varchar(100) NOT NULL DEFAULT '' COMMENT '关联商品id逗号分隔',
  `mall_recommend_class` varchar(100) NOT NULL DEFAULT '' COMMENT '区域首页推荐组id逗号分隔',
  `site_recommend_class` varchar(100) NOT NULL DEFAULT '' COMMENT '店铺首页推荐组id逗号分隔',
  `ctime` bigint(12) unsigned DEFAULT NULL COMMENT '创建时间',
  `utime` bigint(12) unsigned DEFAULT NULL COMMENT '更新时间',
  `isdel` bigint(12) unsigned DEFAULT NULL COMMENT '删除标志 有数据表示删除',
  PRIMARY KEY (`id`),
  UNIQUE KEY `IX_GoodsCode` (`bn`),
  KEY `isdel` (`isdel`),
  KEY `is_hot` (`is_hot`),
  KEY `is_recommend` (`is_recommend`),
  KEY `sort` (`sort`),
  KEY `IX_IMAGE` (`image_id`),
  KEY `IX_GOODS_CATEGORY` (`goods_cat_id`),
  KEY `IX_GOODS_TYPE` (`goods_type_id`),
  KEY `IX_BRAND` (`brand_id`),
  KEY `IX_AREA_MALL_SITE` (`site_id`),
  KEY `IX_AREA_MALL` (`mall_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='商品表';


DROP TABLE IF EXISTS `ev_goods_browsing`;
CREATE TABLE `ev_goods_browsing` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `mall_id` int(10) NOT NULL DEFAULT '0',
  `site_id` int(10) unsigned NOT NULL DEFAULT '0',
  `goods_id` int(10) unsigned DEFAULT '0' COMMENT '商品id 关联goods.id',
  `user_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '用户id',
  `goods_name` varchar(200) NOT NULL COMMENT '商品名称',
  `ctime` bigint(12) NOT NULL COMMENT '浏览时间',
  `isdel` bigint(12) unsigned DEFAULT NULL COMMENT '删除标志',
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`),
  KEY `isdel` (`isdel`),
  KEY `IX_GOODS` (`goods_id`),
  KEY `IX_AREA_MALL_SITE` (`site_id`),
  KEY `IX_AREA_MALL` (`mall_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='商品浏览记录表';


DROP TABLE IF EXISTS `ev_goods_cat`;
CREATE TABLE `ev_goods_cat` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `site_id` int(10) NOT NULL DEFAULT '0',
  `mall_id` int(10) NOT NULL DEFAULT '0',
  `parent_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '上级分类id',
  `name` varchar(20) DEFAULT NULL COMMENT '分类名称',
  `type_id` int(10) unsigned DEFAULT '0' COMMENT '类型ID 关联 goods_type.id',
  `sort` smallint(5) unsigned DEFAULT '100' COMMENT '分类排序 越小越靠前',
  `image_id` char(32) DEFAULT NULL COMMENT '分类图片ID',
  `utime` bigint(12) unsigned DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `IX_PARENT_GOODS_CAT` (`parent_id`),
  KEY `IX_GOODS_TYPE` (`type_id`),
  KEY `IX_IMAGE` (`image_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='商品分类';


DROP TABLE IF EXISTS `ev_goods_collection`;
CREATE TABLE `ev_goods_collection` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `mall_id` smallint(4) unsigned NOT NULL DEFAULT '0',
  `site_id` int(10) unsigned NOT NULL DEFAULT '0',
  `buyer_id` int(10) unsigned NOT NULL COMMENT '用户id',
  `goods_id` int(10) unsigned DEFAULT NULL COMMENT '商品id 关联goods.id',
  `created_at` timestamp NULL DEFAULT NULL COMMENT '商品关注时间',
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `goods_id` (`goods_id`),
  KEY `IX_MALL_USER` (`mall_id`,`buyer_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='商品收藏表';


DROP TABLE IF EXISTS `ev_goods_comment*`;
CREATE TABLE `ev_goods_comment*` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `mall_id` int(10) unsigned NOT NULL DEFAULT '0',
  `site_id` int(10) unsigned NOT NULL DEFAULT '0',
  `comment_id` int(10) unsigned DEFAULT '0' COMMENT '父级评价ID',
  `score` tinyint(1) DEFAULT '5' COMMENT '评价1-5星',
  `user_id` int(10) unsigned NOT NULL COMMENT '评价用户ID',
  `goods_id` int(10) unsigned DEFAULT NULL COMMENT '商品ID 关联goods.id',
  `order_id` bigint(20) unsigned NOT NULL COMMENT '评价订单ID',
  `addon` text COMMENT '货品规格序列号存储',
  `images` varchar(200) DEFAULT NULL COMMENT '评价图片逗号分隔最多五张',
  `content` text COMMENT '评价内容',
  `seller_content` text COMMENT '商家回复',
  `display` tinyint(3) unsigned DEFAULT '1' COMMENT '是否显示 1显示 2不显示',
  `ctime` bigint(12) unsigned DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`),
  KEY `display` (`display`),
  KEY `goods_id` (`goods_id`),
  KEY `IX_AREA_MALL` (`mall_id`),
  KEY `IX_AREA_MALL_SITE` (`site_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='商品评价表';


DROP TABLE IF EXISTS `ev_goods_images`;
CREATE TABLE `ev_goods_images` (
  `goods_id` int(10) unsigned NOT NULL COMMENT '商品ID',
  `image_id` char(32) NOT NULL COMMENT '图片ID',
  KEY `goods_id` (`goods_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='商品图片关联表';


DROP TABLE IF EXISTS `ev_goods_params`;
CREATE TABLE `ev_goods_params` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `mall_id` int(10) NOT NULL DEFAULT '0',
  `site_id` int(10) NOT NULL DEFAULT '0',
  `name` varchar(50) DEFAULT NULL COMMENT '参数名称',
  `value` text COMMENT '参数值',
  `type` varchar(10) DEFAULT NULL COMMENT '参数类型，text文本框，radio单选，checkbox复选框',
  `ctime` bigint(12) DEFAULT NULL COMMENT '创建时间',
  `utime` bigint(12) DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='商品参数表';


DROP TABLE IF EXISTS `ev_goods_type`;
CREATE TABLE `ev_goods_type` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `mall_id` int(10) NOT NULL DEFAULT '0',
  `site_id` int(10) NOT NULL DEFAULT '0',
  `name` varchar(20) DEFAULT NULL COMMENT '类型名称',
  `params` text COMMENT '参数序列号存储 array(参数组名=>array(''参数1'',''参数二''))',
  PRIMARY KEY (`id`),
  KEY `IX_AREA_MALL` (`mall_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='商品类型';


DROP TABLE IF EXISTS `ev_goods_type_params`;
CREATE TABLE `ev_goods_type_params` (
  `params_id` int(10) unsigned DEFAULT '0' COMMENT '商品参数id',
  `type_id` int(10) unsigned DEFAULT '0' COMMENT '商品类型id',
  KEY `IX_PARAMS` (`params_id`),
  KEY `IX_GOODS_TYPE` (`type_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ev_goods_type_spec`;
CREATE TABLE `ev_goods_type_spec` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `mall_id` int(10) NOT NULL DEFAULT '0',
  `site_id` int(10) NOT NULL DEFAULT '0',
  `name` varchar(30) DEFAULT NULL COMMENT '商品类型属性名称',
  `sort` smallint(5) unsigned DEFAULT '100' COMMENT '商品类型属性排序 越小越靠前',
  PRIMARY KEY (`id`),
  KEY `sort` (`sort`),
  KEY `IX_AREA_MALL_SITE` (`site_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='商品类型属性表';


DROP TABLE IF EXISTS `ev_goods_type_spec_rel`;
CREATE TABLE `ev_goods_type_spec_rel` (
  `site_id` int(10) NOT NULL DEFAULT '0',
  `spec_id` int(10) unsigned NOT NULL COMMENT '属性ID',
  `type_id` int(10) unsigned DEFAULT NULL COMMENT '类型ID',
  KEY `IX_GOODS_TYPE` (`type_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='商品类型和属性关联表';


DROP TABLE IF EXISTS `ev_goods_type_spec_value`;
CREATE TABLE `ev_goods_type_spec_value` (
  `site_id` int(10) NOT NULL DEFAULT '0',
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `spec_id` int(10) unsigned DEFAULT NULL COMMENT '属性ID 关联goods_type_spec.id',
  `value` varchar(50) DEFAULT NULL COMMENT '属性值',
  `sort` smallint(5) unsigned DEFAULT '100' COMMENT '排序 越小越靠前',
  PRIMARY KEY (`id`),
  KEY `spec_id` (`spec_id`),
  KEY `sort` (`sort`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='商品类型属性值表';


DROP TABLE IF EXISTS `ev_hooks`;
CREATE TABLE `ev_hooks` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `name` varchar(50) DEFAULT NULL COMMENT '钩子名称',
  `description` text NOT NULL COMMENT '钩子描述',
  `type` tinyint(1) NOT NULL DEFAULT '1' COMMENT '钩子类型,1是控制器，2是视图',
  `addons` text NOT NULL COMMENT '钩子挂载的插件，逗号分隔',
  `ctime` int(10) NOT NULL COMMENT '创建时间',
  `utime` int(11) NOT NULL DEFAULT '0' COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ev_ietask`;
CREATE TABLE `ev_ietask` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(200) DEFAULT NULL COMMENT '任务名称',
  `message` varchar(255) DEFAULT NULL COMMENT '任务消息',
  `file_type` varchar(20) DEFAULT 'csv' COMMENT '文件类型',
  `ctime` int(10) unsigned DEFAULT NULL COMMENT '创建时间',
  `utime` int(10) DEFAULT NULL COMMENT '更新时间',
  `type` tinyint(1) DEFAULT NULL COMMENT '任务类型，1为导出，2为导入',
  `status` tinyint(1) unsigned DEFAULT '0' COMMENT '任务状态，0为等待执行，1正在导出，2导出成功，3导出失败，4正在导入，5导入成功，6导入失败，7中断，8部分导入',
  `params` text COMMENT '相关参数',
  `file_name` varchar(255) DEFAULT NULL COMMENT '文件名称',
  `file_size` varchar(200) DEFAULT NULL COMMENT '文件大小',
  `file_path` varchar(255) DEFAULT NULL COMMENT '文件路径',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='导入导出任务表';


DROP TABLE IF EXISTS `ev_images`;
CREATE TABLE `ev_images` (
  `id` char(32) NOT NULL COMMENT '图片ID',
  `name` varchar(50) DEFAULT NULL COMMENT '图片名称',
  `url` varchar(255) DEFAULT NULL COMMENT '绝对地址',
  `path` varchar(255) DEFAULT NULL COMMENT '物理地址',
  `type` enum('web','local','qiniu') DEFAULT 'local' COMMENT '存储引擎',
  `ctime` bigint(12) unsigned DEFAULT NULL COMMENT '创建时间',
  `isdel` bigint(12) unsigned DEFAULT NULL COMMENT '删除标志 有数据代表删除',
  PRIMARY KEY (`id`),
  UNIQUE KEY `id` (`id`),
  KEY `id_2` (`id`),
  KEY `isdel` (`isdel`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='图片表';


DROP TABLE IF EXISTS `ev_jobs`;
CREATE TABLE `ev_jobs` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `queue` varchar(255) NOT NULL,
  `payload` longtext NOT NULL,
  `attempts` tinyint(3) unsigned NOT NULL,
  `reserved` tinyint(3) unsigned NOT NULL,
  `reserved_at` int(10) unsigned DEFAULT NULL,
  `available_at` int(10) unsigned NOT NULL,
  `created_at` int(10) unsigned NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='队列表';


DROP TABLE IF EXISTS `ev_jwt_api_auth`;
CREATE TABLE `ev_jwt_api_auth` (
  `jwt_auth_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `api_id` varchar(30) NOT NULL COMMENT 'API ID',
  `api_secret` varchar(30) NOT NULL COMMENT 'API SECRET',
  `api_desc` varchar(300) NOT NULL COMMENT 'TARGET FROM WEIXIN | PC BROWSER | ADMIN WEB | Portal',
  `user_class` varchar(50) DEFAULT NULL COMMENT '定义创建token所使用的用户认证类：ClientWebUser | ClientWXUser | AdminWebUser | AdminPortalUser',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`jwt_auth_id`),
  UNIQUE KEY `UK_JWTAPIAUTH_APIID` (`api_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ev_jwt_auth_users`;
CREATE TABLE `ev_jwt_auth_users` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `group_id` smallint(4) unsigned NOT NULL DEFAULT '0' COMMENT 'GROUPAPPID',
  `auth_id` varchar(50) NOT NULL DEFAULT '' COMMENT '用户标识名称，一个用户唯一，前台WEB用户是自动生成ID，前台微信用户是用户的openid，后台为用户名',
  `auth_secret` varchar(50) NOT NULL DEFAULT '' COMMENT '用户所使用的secret，对于后台有效',
  `target_platform` varchar(50) NOT NULL DEFAULT '' COMMENT '来源于哪个平台',
  `jwt_auth_id` int(11) NOT NULL DEFAULT '0' COMMENT 'JWT_API_AUTH数据表外键',
  `user_id` bigint(11) NOT NULL DEFAULT '0' COMMENT 'USER_ID,对于前端用户，在微信登录后，此字段为buyer_wx_login表; 对于后端用户，表示为ev_group_login',
  `remember_token` varchar(512) NOT NULL DEFAULT '' COMMENT '用户TOKEN',
  `token_update_time` int(11) NOT NULL DEFAULT '0' COMMENT 'TOKEN更新时间',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `IDX_UNIQUE_AUTHID` (`auth_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='JWT认证的用户';


DROP TABLE IF EXISTS `ev_label`;
CREATE TABLE `ev_label` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `mall_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'ev_area_mall.mall_id',
  `site_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'ev_area_site.site_id',
  `name` varchar(20) DEFAULT NULL COMMENT '名称',
  `style` varchar(20) DEFAULT 'hot' COMMENT '样式',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='标签表';


DROP TABLE IF EXISTS `ev_login_log`;
CREATE TABLE `ev_login_log` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `mall_id` int(10) unsigned NOT NULL DEFAULT '0',
  `site_id` int(10) unsigned NOT NULL DEFAULT '0',
  `user_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '用户id',
  `state` tinyint(1) DEFAULT NULL COMMENT '登录 1  退出2,3注册',
  `log_time` bigint(12) unsigned DEFAULT NULL COMMENT '时间',
  `city` varchar(100) DEFAULT NULL COMMENT '地点城市',
  `ip` varchar(15) DEFAULT NULL COMMENT 'ip地址',
  PRIMARY KEY (`id`),
  KEY `IX_USER` (`user_id`),
  KEY `IX_AREA_MALL` (`mall_id`),
  KEY `IX_AREA_MALL_SITE` (`site_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


DROP TABLE IF EXISTS `ev_logistics*`;
CREATE TABLE `ev_logistics*` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `logi_name` varchar(30) DEFAULT NULL COMMENT '物流公司名称',
  `logi_code` varchar(50) DEFAULT NULL COMMENT '物流公司编码',
  `sort` tinyint(3) unsigned DEFAULT '100' COMMENT '排序 越小越靠前',
  PRIMARY KEY (`id`),
  UNIQUE KEY `logi_code` (`logi_code`),
  KEY `sort` (`sort`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='物流公司表';


DROP TABLE IF EXISTS `ev_manage`;
CREATE TABLE `ev_manage` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '用户ID',
  `is_root` smallint(3) NOT NULL DEFAULT '0' COMMENT '是否是root账号,0不是1是',
  `mall_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'ev_area_mall.mall_id',
  `site_id` int(10) NOT NULL DEFAULT '0' COMMENT 'ev_area_site.site_id',
  `username` varchar(20) DEFAULT NULL COMMENT '用户名',
  `password` char(32) DEFAULT NULL COMMENT '密码 md5(md5()+创建时间)',
  `mobile` char(15) DEFAULT NULL COMMENT '手机号',
  `avatar` varchar(255) DEFAULT NULL COMMENT '头像',
  `nickname` varchar(50) DEFAULT NULL COMMENT '昵称',
  `ctime` bigint(12) unsigned DEFAULT NULL,
  `utime` bigint(12) unsigned DEFAULT NULL,
  `status` tinyint(1) unsigned DEFAULT '1' COMMENT '1 = 正常 2 = 停用',
  PRIMARY KEY (`id`),
  UNIQUE KEY `username` (`username`),
  KEY `IX_AREA_MALL` (`mall_id`),
  KEY `IX_AREA_MALL_SITE` (`site_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='管理员表';


DROP TABLE IF EXISTS `ev_manage_role`;
CREATE TABLE `ev_manage_role` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(50) DEFAULT NULL COMMENT '角色名称',
  `utime` bigint(12) unsigned DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `id` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='总后台角色表';


DROP TABLE IF EXISTS `ev_manage_role_operation_rel`;
CREATE TABLE `ev_manage_role_operation_rel` (
  `manage_role_id` int(10) NOT NULL,
  `operation_id` int(10) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='店铺角色操作权限关联表';


DROP TABLE IF EXISTS `ev_manage_role_rel`;
CREATE TABLE `ev_manage_role_rel` (
  `manage_id` int(10) unsigned NOT NULL COMMENT '管理员ID 关联manage.id',
  `role_id` int(10) unsigned DEFAULT NULL COMMENT '角色ID 关联role.id',
  KEY `IX_MANAGE_ROLE` (`role_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='管理员和角色关系表';


DROP TABLE IF EXISTS `ev_manage_see_menu`;
CREATE TABLE `ev_manage_see_menu` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `auth_type` smallint(1) NOT NULL COMMENT '1超级管理员 2区域管理员 3店铺管理员',
  `operation_id` int(10) NOT NULL COMMENT '对应的能看到的菜单',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ev_message`;
CREATE TABLE `ev_message` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `user_id` int(10) NOT NULL COMMENT '用户id',
  `code` varchar(60) NOT NULL COMMENT '消息编码',
  `params` varchar(5000) NOT NULL COMMENT '参数',
  `content` text NOT NULL COMMENT '内容',
  `ctime` bigint(12) unsigned NOT NULL COMMENT '创建时间',
  `utime` bigint(12) unsigned NOT NULL COMMENT '查看时间',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '1未查看，2已查看',
  PRIMARY KEY (`id`),
  KEY `user_id` (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='站内信';


DROP TABLE IF EXISTS `ev_message_center`;
CREATE TABLE `ev_message_center` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `code` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '编码',
  `sms` tinyint(1) unsigned NOT NULL COMMENT '1 启用  2禁用',
  `message` tinyint(1) unsigned NOT NULL COMMENT '站内消息',
  `wx_tpl_message` tinyint(1) unsigned NOT NULL COMMENT '微信模板消息',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='消息中心表';


DROP TABLE IF EXISTS `ev_message_template`;
CREATE TABLE `ev_message_template` (
  `mt_id` smallint(4) unsigned NOT NULL AUTO_INCREMENT,
  `type_code` varchar(30) NOT NULL DEFAULT '' COMMENT '消息类型',
  `type_remark` varchar(100) DEFAULT '' COMMENT '类型备注',
  `wx_template_code` varchar(50) NOT NULL DEFAULT '' COMMENT '微信消息模板ID',
  `created_at` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `updated_at` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`mt_id`),
  UNIQUE KEY `IDX_TEMPLATE` (`type_code`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='微信消息模板';


DROP TABLE IF EXISTS `ev_notice*`;
CREATE TABLE `ev_notice*` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `site_id` int(10) unsigned NOT NULL DEFAULT '0',
  `title` varchar(200) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '公告标题',
  `content` longtext CHARACTER SET utf8mb4 COMMENT '公告内容',
  `type` tinyint(1) unsigned DEFAULT '1' COMMENT '公告类型',
  `ctime` bigint(12) DEFAULT NULL COMMENT '创建时间 毫秒',
  `sort` tinyint(3) unsigned DEFAULT '100' COMMENT '排序 从小到大',
  `isdel` bigint(12) unsigned DEFAULT NULL COMMENT '软删除位  有时间代表已删除',
  PRIMARY KEY (`id`),
  KEY `isdel` (`isdel`),
  KEY `sort` (`sort`),
  KEY `IX_AREA_MALL_SITE` (`site_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='公告表';


DROP TABLE IF EXISTS `ev_operation`;
CREATE TABLE `ev_operation` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `parent_id` int(10) unsigned NOT NULL COMMENT '父ID',
  `name` varchar(50) DEFAULT NULL COMMENT '操作名称',
  `code` varchar(50) DEFAULT NULL COMMENT '操作编码',
  `type` enum('m','c','a') NOT NULL DEFAULT 'a' COMMENT '类型',
  `parent_menu_id` int(10) unsigned NOT NULL COMMENT '菜单id',
  `perm_type` int(1) unsigned NOT NULL DEFAULT '3' COMMENT '权限许可类型，如果为1就是主体权限，， 如果为2就是半主体权限，在左侧菜单不显示，但是在权限菜单上有体现，如果为3就是关联权限',
  `sort` tinyint(3) unsigned DEFAULT '100' COMMENT '操作排序 越小越靠前',
  PRIMARY KEY (`id`),
  KEY `parent_id` (`parent_id`),
  KEY `id` (`id`),
  KEY `parent_menu_id` (`parent_menu_id`),
  KEY `sort` (`sort`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='权限表';


DROP TABLE IF EXISTS `ev_operation_log`;
CREATE TABLE `ev_operation_log` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `manage_id` int(10) unsigned DEFAULT NULL COMMENT '管理员ID',
  `controller` varchar(50) DEFAULT NULL COMMENT '操作的控制器名',
  `method` varchar(50) DEFAULT NULL COMMENT '操作方法名',
  `desc` varchar(255) DEFAULT NULL COMMENT '操作描述',
  `content` text COMMENT '操作数据序列号存储',
  `ip` char(50) DEFAULT NULL COMMENT '操作IP',
  `ctime` bigint(12) unsigned DEFAULT NULL COMMENT '操作时间',
  PRIMARY KEY (`id`),
  KEY `IX_MANAGE` (`manage_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='后台操作记录表';


DROP TABLE IF EXISTS `ev_order*`;
CREATE TABLE `ev_order*` (
  `order_id` varchar(20) NOT NULL,
  `mall_id` int(10) NOT NULL DEFAULT '0',
  `site_id` int(10) unsigned NOT NULL DEFAULT '0',
  `goods_amount` decimal(10,2) unsigned DEFAULT '0.00' COMMENT '商品总价',
  `payed` decimal(10,2) unsigned DEFAULT '0.00' COMMENT '已支付的金额',
  `order_amount` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '订单实际销售总额',
  `pay_sn` varchar(20) DEFAULT '' COMMENT '支付单号',
  `pay_status` tinyint(1) unsigned DEFAULT '1' COMMENT '支付状态 1=未付款 2=已付款 3=部分付款 4=部分退款 5=已退款',
  `ship_status` tinyint(1) unsigned DEFAULT '1' COMMENT '发货状态 1=未发货 2=部分发货 3=已发货 4=部分退货 5=已退货',
  `status` tinyint(1) unsigned DEFAULT '1' COMMENT '订单状态 1=正常 2=完成 3=取消',
  `payment_code` varchar(20) DEFAULT NULL COMMENT '支付方式代码',
  `payment_time` bigint(12) DEFAULT NULL COMMENT '支付时间',
  `logistics_id` varchar(20) DEFAULT NULL COMMENT '配送方式ID 关联ship.id',
  `logistics_name` varchar(50) DEFAULT NULL COMMENT '配送方式名称',
  `cost_freight` decimal(6,2) unsigned DEFAULT '0.00' COMMENT '配送费用',
  `user_id` int(10) unsigned DEFAULT NULL COMMENT '用户ID 关联user.id',
  `seller_id` int(10) unsigned DEFAULT NULL COMMENT '店铺ID 关联seller.id',
  `confirm` tinyint(1) unsigned DEFAULT '1' COMMENT '售后状态 1=未确认收货 2=已确认收货',
  `confirm_time` bigint(12) unsigned DEFAULT NULL COMMENT '确认收货时间',
  `ship_area_id` int(10) unsigned DEFAULT '0' COMMENT '收货地区ID',
  `ship_address` varchar(200) DEFAULT NULL COMMENT '收货详细地址',
  `ship_name` varchar(50) DEFAULT NULL COMMENT '收货人姓名',
  `ship_mobile` char(15) DEFAULT NULL COMMENT '收货电话',
  `weight` double(10,2) unsigned DEFAULT '0.00' COMMENT '商品总重量',
  `tax_type` tinyint(1) unsigned DEFAULT '1' COMMENT '是否开发票 1=不发票 2=个人发票 3=公司发票',
  `tax_content` varchar(255) DEFAULT '商品详情' COMMENT '发票内容',
  `tax_code` varchar(50) DEFAULT NULL COMMENT '税号',
  `tax_title` varchar(50) DEFAULT NULL COMMENT '发票抬头',
  `point` int(10) unsigned DEFAULT NULL,
  `point_money` decimal(10,2) DEFAULT NULL,
  `order_pmt` decimal(10,2) unsigned DEFAULT '0.00' COMMENT '订单优惠金额',
  `goods_pmt` decimal(10,2) unsigned DEFAULT '0.00' COMMENT '商品优惠金额',
  `coupon_pmt` decimal(10,2) unsigned DEFAULT '0.00' COMMENT '优惠券优惠额度',
  `coupon` varchar(5000) DEFAULT NULL COMMENT '优惠券信息',
  `promotion_list` varchar(255) DEFAULT NULL COMMENT '优惠信息',
  `memo` varchar(255) DEFAULT NULL COMMENT '买家备注',
  `ip` varchar(50) DEFAULT NULL COMMENT '下单IP',
  `mark` varchar(255) DEFAULT NULL COMMENT '卖家备注',
  `source` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '订单来源 1=PC页面 2=H5页面 3=微信小程序',
  `is_comment` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '是否评论，1未评论，2已评论',
  `ctime` bigint(12) unsigned DEFAULT NULL COMMENT '创建时间',
  `utime` bigint(12) unsigned DEFAULT NULL COMMENT '更新时间',
  `isdel` bigint(12) unsigned DEFAULT NULL COMMENT '删除标志 有数据表示删除',
  PRIMARY KEY (`order_id`),
  KEY `IX_USER` (`user_id`),
  KEY `IX_AREA_MALL_SITE` (`site_id`),
  KEY `IX_AREA_MALL` (`mall_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='订单表';


DROP TABLE IF EXISTS `ev_order_buyer_info`;
CREATE TABLE `ev_order_buyer_info` (
  `obi_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `mall_id` smallint(4) unsigned NOT NULL DEFAULT '0' COMMENT '分站ID=>FKEY(ev_area_mall)',
  `pay_sn` varchar(50) NOT NULL DEFAULT '' COMMENT '支付单号',
  `buyer_id` int(10) NOT NULL DEFAULT '0' COMMENT '买家ID=>FKEY(ev_buyer)',
  `receiver_name` varchar(30) NOT NULL DEFAULT '' COMMENT '收货人姓名',
  `receiver_address_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '买家地址ID=>FKEY(ev_data_area)',
  `receiver_province` varchar(30) NOT NULL DEFAULT '' COMMENT '收货地址省',
  `receiver_city` varchar(30) NOT NULL DEFAULT '' COMMENT '收货地址市',
  `receiver_area` varchar(30) NOT NULL DEFAULT '' COMMENT '收货地址区',
  `receiver_address_info` varchar(255) NOT NULL DEFAULT '' COMMENT '详细收货地址',
  `receiver_tel` varchar(30) NOT NULL DEFAULT '' COMMENT '买家联系手机号',
  `receiver_floor_num` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '所在楼层',
  `created_at` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `updated_at` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`obi_id`),
  UNIQUE KEY `UK_OBI_ORDER_SN` (`pay_sn`),
  KEY `IX_AREA_MALL` (`mall_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='订单中的买家信息表';


DROP TABLE IF EXISTS `ev_order_common`;
CREATE TABLE `ev_order_common` (
  `order_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `mall_id` smallint(4) unsigned NOT NULL DEFAULT '0' COMMENT '分站ID=>FKEY(ev_area_mall)',
  `site_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '网点ID=>FKEY(ev_area_mall_site)',
  `buyer_id` int(11) unsigned DEFAULT '0' COMMENT '买家ID=>FKEY(ev_buyer)',
  `order_type` enum('goods_order','points_order') NOT NULL COMMENT '订单类型',
  `order_sn` varchar(50) NOT NULL COMMENT '商城生成的订单编号',
  `pay_sn` varchar(50) DEFAULT '' COMMENT '支付单号',
  `order_add_time` int(11) NOT NULL DEFAULT '0' COMMENT '订单生成时间',
  `order_pay_time` int(11) unsigned DEFAULT '0' COMMENT '订单支付完成时间',
  `order_payment_code` varchar(30) DEFAULT '' COMMENT '只记录最后的支付方式，微信-WEIXIN_PAY 积分-POINT_PAY 余额-VCOUNT_PAY 优惠券-COUPON_PAY',
  `order_price` decimal(16,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '订单的价格',
  `order_point_price` int(10) unsigned NOT NULL DEFAULT '0',
  `order_origin_price` decimal(16,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '订单所需的原价',
  `order_full_activity_amount` decimal(16,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '满减金额',
  `order_shipping_fee` decimal(16,2) NOT NULL DEFAULT '0.00' COMMENT '订单的运费价格',
  `order_state` tinyint(4) unsigned DEFAULT '0' COMMENT '-- 0. 待支付，10. 已取消（用户取消）  20. 已取消(30分钟系统取消)   30. 待发货   40.  待收货  50. 已收货(待评价)  60.已完成',
  `order_state_remark` varchar(100) DEFAULT '' COMMENT '订单状态备注',
  `order_remark` varchar(1000) DEFAULT '' COMMENT '买家订单留言',
  `order_floor_num` smallint(2) unsigned NOT NULL DEFAULT '0' COMMENT '送货楼层',
  `order_floor_fee` decimal(6,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '挑楼费',
  `is_need_floor` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否需要挑楼',
  `is_all_return` tinyint(1) unsigned DEFAULT '0' COMMENT '是否全部退款/退货，是的话，不显示在导购的未支付订单列表',
  `is_send_msg` tinyint(1) unsigned DEFAULT '0' COMMENT '支付完是否发送过消息给导购，1已发，0未发',
  `created_at` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `updated_at` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`order_id`),
  UNIQUE KEY `UK_ORDER_ORDERSN` (`order_sn`),
  KEY `IX_AREA_MALL` (`mall_id`),
  KEY `IX_AREA_MALL_SITE` (`site_id`),
  KEY `IX_ORDER_TYPE` (`order_type`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='订单主表';


DROP TABLE IF EXISTS `ev_order_goods`;
CREATE TABLE `ev_order_goods` (
  `osp_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '订单商品ID',
  `mall_id` smallint(4) unsigned NOT NULL DEFAULT '0' COMMENT '分站ID=>FKEY(ev_area_mall)',
  `site_id` smallint(4) unsigned NOT NULL DEFAULT '0' COMMENT '网点ID=>FKEY(ev_area_mall_site)',
  `order_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '订单ID=>FKEY(ev_order_common)',
  `goods_id` int(10) NOT NULL DEFAULT '0' COMMENT 'FKEY=>ev_goods',
  `goods_name` varchar(255) NOT NULL DEFAULT '' COMMENT '商品名称',
  `goods_spec_value` varchar(100) DEFAULT '' COMMENT '商品规格名称，如红色 35',
  `products_id` int(10) DEFAULT '0' COMMENT 'FKEY=>ev_products',
  `goods_num` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '商品数量',
  `goods_send_num` int(10) unsigned DEFAULT '0' COMMENT '商品已发货数量',
  `osp_price` decimal(16,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '商品所需人民币价格',
  `osp_point_price` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '积分价格',
  `osp_origin_price` decimal(16,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '原价',
  `osp_full_price` decimal(16,2) DEFAULT '0.00' COMMENT '满减后的价格',
  `goods_image` varchar(255) DEFAULT '' COMMENT '商品图片',
  `discount_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '折扣活动ID',
  `full_delivery_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '满减活动ID',
  `created_at` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `updated_at` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`osp_id`),
  KEY `IDX_ORDER_ID` (`order_id`),
  KEY `IX_SITE` (`site_id`),
  KEY `IX_GOODS` (`goods_id`),
  KEY `IX_PRODUCTS` (`products_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AVG_ROW_LENGTH=121 COMMENT='订单与商品关联表';


DROP TABLE IF EXISTS `ev_order_items*`;
CREATE TABLE `ev_order_items*` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `order_id` varchar(20) NOT NULL COMMENT '订单ID 关联order.id',
  `goods_id` int(10) unsigned DEFAULT NULL COMMENT '商品ID 关联goods.id',
  `product_id` int(10) unsigned DEFAULT NULL COMMENT '货品ID 关联products.id',
  `sn` varchar(30) DEFAULT NULL COMMENT '货品编码',
  `bn` varchar(30) DEFAULT NULL COMMENT '商品编码',
  `name` varchar(200) DEFAULT NULL COMMENT '商品名称',
  `price` decimal(10,2) unsigned DEFAULT NULL COMMENT '货品价格单价',
  `costprice` decimal(10,2) unsigned DEFAULT NULL COMMENT '货品成本价单价',
  `mktprice` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '市场价',
  `image_url` varchar(100) NOT NULL COMMENT '图片',
  `nums` smallint(5) unsigned DEFAULT NULL COMMENT '数量',
  `amount` decimal(10,2) unsigned DEFAULT NULL COMMENT '总价',
  `promotion_amount` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '商品优惠总金额',
  `promotion_list` varchar(255) NOT NULL COMMENT '促销信息',
  `weight` decimal(10,2) DEFAULT NULL COMMENT '总重量',
  `sendnums` smallint(5) unsigned DEFAULT NULL COMMENT '发货数量',
  `addon` text COMMENT '货品明细序列号存储',
  `utime` bigint(12) unsigned DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `IX_ORDER` (`order_id`),
  KEY `IX_GOODS` (`goods_id`),
  KEY `IX_PRODUCT` (`product_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='订单明细表';


DROP TABLE IF EXISTS `ev_order_log`;
CREATE TABLE `ev_order_log` (
  `olp_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '订单日志ID',
  `mall_id` smallint(4) unsigned NOT NULL DEFAULT '0',
  `order_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '订单ID=>FKEY(ev_order_common)',
  `order_sn` varchar(30) DEFAULT '' COMMENT '订单号',
  `log_msg` varchar(200) DEFAULT '' COMMENT '文字描述',
  `log_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '处理时间',
  `log_orderstate` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '订单状态：0(已取消)10:未付款;20:已付款;30:已发货;40:已收货; 80:已退款',
  `create_person` varchar(255) DEFAULT '' COMMENT '添加人',
  `created_at` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `updated_at` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`olp_id`),
  KEY `IDX_ORDER_ID` (`order_id`),
  KEY `IX_MALL` (`mall_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AVG_ROW_LENGTH=120 COMMENT='订单处理历史表';


DROP TABLE IF EXISTS `ev_order_package`;
CREATE TABLE `ev_order_package` (
  `op_id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '包裹Id',
  `mall_id` smallint(4) unsigned NOT NULL DEFAULT '0' COMMENT '分站ID=>FKEY(ev_area_mall)',
  `site_id` smallint(4) unsigned NOT NULL DEFAULT '0' COMMENT '网点ID=>FKEY(ev_group_site)',
  `order_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '订单ID=>FKEY(ev_order_common)',
  `order_sn` varchar(30) DEFAULT '' COMMENT '订单号',
  `package_state` tinyint(3) NOT NULL DEFAULT '0' COMMENT '包裹状态 0未知 2：提货  3 发货 4 验货 5收货',
  `shipping_type` enum('free','post','express','virtual','unknown') NOT NULL DEFAULT 'unknown' COMMENT '交易物流方式: free(包邮),post(平邮), express(快递), virtual(虚拟发货)',
  `shipping_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '配送时间',
  `shipping_express_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '配送公司ID=>FKEY(ev_data_express)',
  `shipping_express_name` varchar(50) NOT NULL DEFAULT '' COMMENT '配送公司名称',
  `shipping_express_code` varchar(50) NOT NULL DEFAULT '' COMMENT '配送公司代码，快递100接口返回',
  `shipping_code` varchar(30) NOT NULL DEFAULT '' COMMENT '配送编号(物流编号)',
  `shipping_state` tinyint(4) NOT NULL DEFAULT '99' COMMENT '物流状态，物流接口返回，0在途中、1已揽收、2疑难、3已签收、4退签、5同城派送中、6退回、7转单，99未知',
  `shipping_note` varchar(200) NOT NULL DEFAULT '' COMMENT '发货备注',
  `package_sn` varchar(32) NOT NULL DEFAULT '' COMMENT '包裹编号',
  `data_from` enum('SELF_DATA','VENDOR_DATA') NOT NULL DEFAULT 'SELF_DATA' COMMENT '如对接第三方，则数据为VENDOR_DATA',
  `created_at` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `updated_at` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`op_id`),
  KEY `OP_INDEX_ORDER_ID` (`order_id`),
  KEY `OP_INDEX_PACKAGE_STATE` (`package_state`) USING BTREE,
  KEY `OP_INDEX_PACKAGE_SN` (`package_sn`),
  KEY `OP_AREA_MALL` (`mall_id`),
  KEY `IX_AREA_MALL_SITE` (`site_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='订单包裹数据表';


DROP TABLE IF EXISTS `ev_order_package_detail`;
CREATE TABLE `ev_order_package_detail` (
  `opd_id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '包裹明细Id',
  `mall_id` smallint(4) unsigned NOT NULL DEFAULT '0' COMMENT '分站ID=>FKEY(ev_area_mall)',
  `order_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '订单ID=>FKEY(ev_order_common)',
  `op_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '包裹Id=>FKEY(ev_order_package)',
  `goods_num` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '商品数量',
  `spd_id` int(10) DEFAULT '0' COMMENT '商品规格ID',
  `osp_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '订单商品ID=>FKEY(ev_order_goods)',
  `created_at` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `updated_at` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`opd_id`),
  KEY `OPD_INDEX_PACKAGE_ID` (`op_id`),
  KEY `IX_OSP_ID` (`osp_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='订单包裹明细表';


DROP TABLE IF EXISTS `ev_order_package_shiplog`;
CREATE TABLE `ev_order_package_shiplog` (
  `ops_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '订单物流日志ID',
  `mall_id` smallint(4) unsigned NOT NULL DEFAULT '0' COMMENT '分站ID=>FKEY(ev_area_mall)',
  `order_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '订单ID=>FKEY(ev_order_common)',
  `shipping_code` varchar(50) NOT NULL DEFAULT '' COMMENT '物流编号',
  `slog_msg` varchar(200) NOT NULL DEFAULT '' COMMENT '文字描述',
  `op_id` int(10) NOT NULL DEFAULT '0' COMMENT '包裹ID=>FKEY(ev_order_package)',
  `slog_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '处理时间',
  `slog_orderstate` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '物流状态，根据物流接口进行定义',
  `create_person` varchar(255) DEFAULT '' COMMENT '添加人',
  `created_at` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `updated_at` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`ops_id`),
  UNIQUE KEY `IX_OP_ID` (`op_id`),
  KEY `IX_ORDER_ID` (`order_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='订单物流日志表，保存快递100接口返回的最后一条记录，实际的数据还是需要到快递100接口查询';


DROP TABLE IF EXISTS `ev_order_pay`;
CREATE TABLE `ev_order_pay` (
  `op_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '订单支付ID',
  `mall_id` smallint(4) unsigned NOT NULL DEFAULT '0' COMMENT '分站ID=>FKEY(ev_area_mall)',
  `order_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '订单ID=>FKEY(ev_order_common)',
  `pay_sn` varchar(100) NOT NULL DEFAULT '' COMMENT '详细支付单号,默认orderSN+opdId',
  `api_pay_state` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '0默认未支付 1已锁定 2已支付(只有第三方支付接口通知到时才会更改此状态)',
  `pay_type_id` tinyint(3) NOT NULL DEFAULT '0' COMMENT '支付类型ID; 1(微信支付),1(ALI_PAY),2(COUPON_PAY),4(POINT_PAY),5(VCOUNT_PAY余额支付)',
  `pay_type_code` enum('WEIXIN_PAY','ALI_PAY','POINT_PAY','COUPON_PAY','VCOUNT_PAY') NOT NULL DEFAULT 'WEIXIN_PAY' COMMENT '支付类型代码',
  `pay_price` decimal(16,2) NOT NULL DEFAULT '0.00' COMMENT '支付金额(含运费)',
  `pay_points` decimal(16,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '支付的积分值',
  `coupon_code` varchar(100) DEFAULT '' COMMENT '此付的优惠券券码',
  `pay_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '支付(付款)时间',
  `v_pay_code` varchar(255) DEFAULT '' COMMENT '收款方的订单号',
  `created_at` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `updated_at` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`op_id`),
  KEY `IDX_ORDER_ID` (`order_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='订单支付详细表,一张订单可以分成多种方式支付，所以订单表和此表的关系是一对多的关系。';


DROP TABLE IF EXISTS `ev_order_pay_log`;
CREATE TABLE `ev_order_pay_log` (
  `opl_id` int(11) NOT NULL AUTO_INCREMENT,
  `pay_sn` varchar(30) DEFAULT NULL COMMENT '支付单号',
  `pay_param` text COMMENT '传值',
  `pay_price` decimal(10,2) DEFAULT NULL COMMENT '订单金额',
  `pay_state` tinyint(1) DEFAULT '0' COMMENT '支付状态:0待支付，1已回调，2已支付',
  `v_pay_code` varchar(100) DEFAULT NULL COMMENT '外部交易号',
  `create_time` datetime DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `updated_at` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`opl_id`),
  KEY `INDEX_ORDER_CODE` (`pay_sn`),
  KEY `INDEX_OPERATE_STATE` (`pay_state`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ev_order_product_evaluate`;
CREATE TABLE `ev_order_product_evaluate` (
  `ose_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '店辅商品评价ID',
  `mall_id` varchar(30) NOT NULL DEFAULT '' COMMENT 'ID',
  `site_id` int(30) unsigned NOT NULL DEFAULT '0' COMMENT '站点ID',
  `order_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '订单ID',
  `order_sn` varchar(60) NOT NULL DEFAULT '' COMMENT '订单号',
  `buyer_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '买家ID=>evshop_buyer',
  `buyer_name` varchar(30) NOT NULL DEFAULT '' COMMENT '买家名字',
  `buyer_header` varchar(500) NOT NULL DEFAULT '',
  `spc_common_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '店辅中商品共通ID=>evshop_spc_common_default)',
  `goods_spec_value` varchar(100) NOT NULL DEFAULT '' COMMENT '商品规格',
  `spd_detail_id` int(10) NOT NULL DEFAULT '0' COMMENT '店辅中商品共通ID=>evshop_spd)',
  `geval_scores` tinyint(3) unsigned NOT NULL DEFAULT '5' COMMENT '评价分数,1-5',
  `evaluate_content` varchar(500) NOT NULL DEFAULT '' COMMENT '评价内容',
  `evaluate_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '评价时间',
  `order_add_time` int(10) NOT NULL DEFAULT '0' COMMENT '下单时间',
  `evaluate_pics` varchar(2000) DEFAULT '' COMMENT '论论多张图片地址，序列化',
  `is_top` tinyint(1) unsigned DEFAULT '0' COMMENT '是否置顶，1是，0否',
  `isanonymous` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '是否匿名评价,0表示否，1表示是',
  `evaluate_remark` varchar(500) NOT NULL DEFAULT '' COMMENT '管理员回复',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `is_delete` tinyint(4) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ose_id`),
