  KEY `IDX_SPC_ORDER_ID` (`order_id`),
  KEY `IX_MALL` (`mall_id`),
  KEY `IX_SITE` (`site_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='店铺商品评价表';


DROP TABLE IF EXISTS `ev_order_return`;
CREATE TABLE `ev_order_return` (
  `return_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `mall_id` smallint(4) DEFAULT '0' COMMENT '分站ID=>FKEY(ev_area_mall)',
  `site_id` smallint(4) unsigned NOT NULL DEFAULT '0' COMMENT '店铺ID=>FKEY(ev_area_mall_site)',
  `order_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '订单ID=>FKEY(ev_order_common)',
  `order_sn` varchar(60) NOT NULL DEFAULT '' COMMENT '订单号',
  `return_sn` varchar(25) DEFAULT '' COMMENT '退单号',
  `buyer_id` int(10) unsigned DEFAULT '0' COMMENT '买家ID=>FKEY(ev_buyer)',
  `refund_type` tinyint(3) DEFAULT '0' COMMENT '区分退货退款来源  0：退款  1：退货',
  `refund_price` decimal(16,2) DEFAULT '0.00' COMMENT '实际退款金额',
  `return_shipping_fee` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '退货运费',
  `return_floor_fee` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '退货挑楼费',
  `return_handing_fee` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '退货装卸费',
  `is_come_get` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否上门取件',
  `osp_price` decimal(16,2) DEFAULT '0.00' COMMENT '原退款金额',
  `phone` varchar(20) DEFAULT '' COMMENT '联系电话',
  `address` varchar(255) DEFAULT '' COMMENT '退货地址',
  `shipping_express_id` int(11) DEFAULT '0' COMMENT '物流公司ID=>FKEY(ev_data_express)',
  `shipping_express_name` varchar(30) DEFAULT NULL COMMENT '物流公司名称',
  `shipping_code` varchar(30) DEFAULT '' COMMENT '物流单号',
  `shipping_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '物流发货时间',
  `explain` varchar(500) DEFAULT '' COMMENT '备注',
  `return_reason` varchar(500) DEFAULT '' COMMENT '退款退货理由',
  `problem_desc` varchar(500) DEFAULT '' COMMENT '问题描述',
  `status` int(11) NOT NULL DEFAULT '1' COMMENT '状态  1：申请退货 （待审核） 2：审核退货成功，退货中 3审核退货失败  4：退货成功 5：退货失败 6:申请退款中，待审核 7:审核退款成功，等待退款 8：退款成功 9: 退款失败 10: 取消退货 11: 取消退款',
  `reason` varchar(500) DEFAULT '' COMMENT '审核原因',
  `is_goods_send` tinyint(1) unsigned DEFAULT '0' COMMENT '货状态，1已发货，0未发货',
  `check_time` int(11) DEFAULT '0' COMMENT '审核时间',
  `create_time` int(11) DEFAULT '0' COMMENT '申请时间',
  `contact_person` varchar(30) NOT NULL DEFAULT '' COMMENT '联系人',
  `cancel_time` int(11) DEFAULT '0' COMMENT '取消时间',
  `sync_status` tinyint(3) DEFAULT '0' COMMENT '浪沙同步状态  0：未同步  1：提交给浪沙 2：取消退单 3:浪沙审核通过并接受收货地址 4：客人已发货',
  `vendor_post_num` smallint(4) unsigned NOT NULL DEFAULT '0',
  `created_at` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `updated_at` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`return_id`),
  UNIQUE KEY `UK_RETURN_SN` (`return_sn`),
  KEY `INDEX_STATUS` (`status`),
  KEY `INDEX_SYNC_STATUS` (`sync_status`),
  KEY `INDEX_ORDER_ID` (`order_id`),
  KEY `INDEX_REFUND_TYPE` (`refund_type`),
  KEY `INDEX_SHIPPING_CODE` (`shipping_code`),
  KEY `IX_GROUP` (`mall_id`),
  KEY `IX_SITE` (`site_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='订单退货/退款表';


DROP TABLE IF EXISTS `ev_order_return_goods`;
CREATE TABLE `ev_order_return_goods` (
  `org_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `return_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '退款单ID=>FKEY(ev_order_return)',
  `return_num` int(4) unsigned NOT NULL DEFAULT '0' COMMENT '退货商品数量',
  `opd_id` int(11) unsigned DEFAULT '0' COMMENT '包裹ID=>FKEY(ev_order_package)',
  `osp_id` int(11) unsigned DEFAULT '0' COMMENT '订单商品明细ID=>FKEY(ev_order_goods)',
  `created_at` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `updated_at` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`org_id`),
  KEY `INX_RETURN_ID` (`return_id`),
  KEY `IX_OSP_ID` (`osp_id`),
  KEY `IX_OPD_ID` (`opd_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ev_order_return_reason`;
CREATE TABLE `ev_order_return_reason` (
  `reason_id` smallint(4) unsigned NOT NULL AUTO_INCREMENT,
  `reason_type` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '类型：1退款，2退货',
  `reason_title` varchar(500) DEFAULT '' COMMENT '退款退货原因',
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `updated_at` timestamp NOT NULL DEFAULT '2018-12-31 07:59:59' ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`reason_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='退款退货原因';


DROP TABLE IF EXISTS `ev_page_goods_class`;
CREATE TABLE `ev_page_goods_class` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `mall_id` int(10) NOT NULL,
  `site_id` int(10) NOT NULL,
  `name` varchar(20) NOT NULL DEFAULT '',
  `style` varchar(20) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ev_page_site_class`;
CREATE TABLE `ev_page_site_class` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `mall_id` int(10) NOT NULL,
  `name` varchar(20) NOT NULL DEFAULT '',
  `style` varchar(20) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ev_payments`;
CREATE TABLE `ev_payments` (
  `id` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `mall_id` int(10) unsigned NOT NULL DEFAULT '0',
  `code` varchar(50) DEFAULT NULL COMMENT '支付类型编码',
  `name` varchar(50) DEFAULT NULL COMMENT '支付类型名称',
  `is_online` tinyint(1) unsigned DEFAULT '1' COMMENT '是否线上支付 1=线上支付 2=线下支付',
  `params` text NOT NULL COMMENT '参数',
  `sort` smallint(5) unsigned NOT NULL DEFAULT '100' COMMENT '排序',
  `memo` varchar(200) NOT NULL COMMENT '支付方式描述',
  `status` smallint(1) unsigned NOT NULL DEFAULT '2' COMMENT '启用状态 1=启用 2=停用',
  PRIMARY KEY (`id`),
  KEY `status` (`status`),
  KEY `IX_MALL` (`mall_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='支付方式表';


DROP TABLE IF EXISTS `ev_products`;
CREATE TABLE `ev_products` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '货品ID',
  `mall_id` int(10) NOT NULL DEFAULT '0',
  `site_id` int(10) unsigned NOT NULL DEFAULT '0',
  `goods_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '商品id 关联goods.id',
  `barcode` varchar(128) DEFAULT NULL COMMENT '货品条码,暂时没用',
  `sn` varchar(30) NOT NULL DEFAULT '' COMMENT '商品条码',
  `price` decimal(10,2) NOT NULL COMMENT '货品价格',
  `price1` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '一级会员价格(不设置代表按照会员折扣来算)',
  `price2` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '二级会员价格(不设置代表按照会员折扣来算)',
  `price3` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '三级会员价格(不设置代表按照会员折扣来算)',
  `price4` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '四级会员价格(不设置代表按照会员折扣来算)',
  `price5` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '五级会员价格(不设置代表按照会员折扣来算)',
  `price6` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '六级会员价格(不设置代表按照会员折扣来算)',
  `marketable` tinyint(1) unsigned DEFAULT NULL COMMENT '上架标志 1=上架 2=下架',
  `stock` int(11) DEFAULT '0' COMMENT '库存',
  `freeze_stock` int(11) DEFAULT '0' COMMENT '冻结库存',
  `spes_desc` text COMMENT '规格值逗号分隔存储',
  `is_defalut` tinyint(1) unsigned DEFAULT '2' COMMENT '是否默认货品 1=是 2=否',
  `isdel` bigint(12) unsigned DEFAULT NULL COMMENT '删除标志',
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `IX_GOODS_SKU` (`sn`),
  KEY `isdel` (`isdel`),
  KEY `goods_id` (`goods_id`),
  KEY `IX_SITE` (`site_id`),
  KEY `IX_MALL` (`mall_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='货品表';


DROP TABLE IF EXISTS `ev_promotion`;
CREATE TABLE `ev_promotion` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `mall_id` int(10) NOT NULL DEFAULT '0',
  `site_id` int(10) NOT NULL DEFAULT '0',
  `name` varchar(40) NOT NULL COMMENT '促销名称',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '启用状态，1开启，2关闭',
  `type` smallint(1) unsigned NOT NULL DEFAULT '1' COMMENT '类型：1促销，2优惠券，3团购，4秒杀',
  `sort` smallint(5) unsigned NOT NULL DEFAULT '100' COMMENT '排序',
  `exclusive` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '排他，1不排他，2排他',
  `auto_receive` smallint(1) unsigned NOT NULL DEFAULT '1' COMMENT '当时优惠券的时候，自动是否自动领取，1自动领取，2不自动领取',
  `params` text COMMENT '其它参数',
  `stime` bigint(12) unsigned NOT NULL COMMENT '开始时间',
  `etime` bigint(12) unsigned NOT NULL COMMENT '结束时间',
  `isdel` bigint(12) unsigned DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `isdel` (`isdel`),
  KEY `sort` (`sort`),
  KEY `status` (`status`),
  KEY `IX_MALL` (`mall_id`),
  KEY `IX_SITE` (`site_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='促销表';


DROP TABLE IF EXISTS `ev_promotion_condition`;
CREATE TABLE `ev_promotion_condition` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `promotion_id` int(10) unsigned DEFAULT NULL COMMENT '促销ID',
  `code` varchar(50) DEFAULT NULL COMMENT '促销条件编码',
  `params` text COMMENT '支付配置参数序列号存储',
  PRIMARY KEY (`id`),
  KEY `promotion_id` (`promotion_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='促销条件表';


DROP TABLE IF EXISTS `ev_promotion_result`;
CREATE TABLE `ev_promotion_result` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `promotion_id` int(10) unsigned DEFAULT NULL COMMENT '促销ID',
  `code` varchar(50) DEFAULT NULL COMMENT '促销条件编码',
  `params` text COMMENT '支付配置参数序列号存储',
  PRIMARY KEY (`id`),
  KEY `promotion_id` (`promotion_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='促销结果表';


DROP TABLE IF EXISTS `ev_setting`;
CREATE TABLE `ev_setting` (
  `skey` varchar(50) NOT NULL DEFAULT '' COMMENT '键',
  `value` text COMMENT '值',
  PRIMARY KEY (`skey`),
  KEY `skey` (`skey`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='店铺设置表';


DROP TABLE IF EXISTS `ev_ship`;
CREATE TABLE `ev_ship` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `site_id` int(10) unsigned NOT NULL DEFAULT '0',
  `name` varchar(50) DEFAULT NULL COMMENT '配送方式名称',
  `has_cod` tinyint(1) unsigned DEFAULT '1' COMMENT '是否货到付款 1=不是货到付款 2=是货到付款',
  `firstunit` mediumint(8) unsigned DEFAULT NULL COMMENT '首重',
  `continueunit` mediumint(8) unsigned DEFAULT NULL COMMENT '续重',
  `def_area_fee` tinyint(1) unsigned DEFAULT '1' COMMENT '按地区设置配送费用是否启用默认配送费用 1=启用 2=不启用',
  `type` tinyint(1) unsigned DEFAULT '1' COMMENT '地区类型 1=全部地区 2=指定地区',
  `firstunit_price` decimal(10,2) unsigned DEFAULT NULL COMMENT '首重费用',
  `continueunit_price` decimal(10,2) unsigned DEFAULT NULL COMMENT '续重费用',
  `exp` text COMMENT '配送费用计算表达式',
  `logi_name` varchar(50) DEFAULT NULL COMMENT '物流公司名称',
  `logi_code` varchar(50) DEFAULT NULL COMMENT '物流公司编码',
  `is_def` tinyint(1) unsigned DEFAULT '2' COMMENT '是否默认 1=默认 2=不默认',
  `sort` smallint(5) unsigned DEFAULT '100' COMMENT '配送方式排序 越小越靠前',
  `status` tinyint(1) unsigned DEFAULT '1' COMMENT '状态 1=正常 2=停用',
  `free_postage` tinyint(1) unsigned DEFAULT '2' COMMENT '是否包邮，1包邮，2不包邮',
  `area_fee` text COMMENT '地区配送费用',
  `goodsmoney` decimal(20,2) DEFAULT '0.00' COMMENT '商品总额满多少免运费',
  PRIMARY KEY (`id`),
  KEY `sort` (`sort`),
  KEY `sort_2` (`sort`),
  KEY `IX_SITE` (`site_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='配送方式表';


DROP TABLE IF EXISTS `ev_site_collection`;
CREATE TABLE `ev_site_collection` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `mall_id` smallint(4) unsigned NOT NULL DEFAULT '0',
  `site_id` int(10) unsigned NOT NULL DEFAULT '0',
  `buyer_id` int(10) unsigned NOT NULL COMMENT '用户id',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `IX_MALL_USER` (`mall_id`,`buyer_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='店铺收藏表';


DROP TABLE IF EXISTS `ev_site_member`;
CREATE TABLE `ev_site_member` (
  `sm_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `site_id` int(10) NOT NULL DEFAULT '0',
  `wx_open_id` varchar(500) NOT NULL COMMENT '微信openID',
  `buyer_id` int(10) NOT NULL,
  `buyer_name` varchar(10) DEFAULT NULL COMMENT '买家实名信息(后台可编辑保存，编辑标识)',
  `buyer_tel` varchar(11) DEFAULT NULL COMMENT '买家联系电话(不可修改)',
  `buyer_level` enum('0','1','2','3','4','5','6') DEFAULT '0' COMMENT '会员等级(对应ev_site_member_level_config中的等级，0除外)',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`sm_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ev_site_member_level_config`;
CREATE TABLE `ev_site_member_level_config` (
  `smlc_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `site_id` int(10) NOT NULL COMMENT 'ev_area_mall_site',
  `level` enum('1','2','3','4','5','6') NOT NULL COMMENT '会员等级',
  `level_name` varchar(40) NOT NULL DEFAULT '' COMMENT '等级名称',
  `level_discount` decimal(10,2) NOT NULL DEFAULT '1.00' COMMENT '会员折扣',
  PRIMARY KEY (`smlc_id`),
  UNIQUE KEY `s_l_u` (`site_id`,`level`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ev_site_member_level_config_old`;
CREATE TABLE `ev_site_member_level_config_old` (
  `smlc_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `site_id` int(10) NOT NULL COMMENT 'ev_area_mall_site',
  `level1` decimal(10,2) NOT NULL DEFAULT '1.00' COMMENT '会员等级1折扣',
  `name1` varchar(40) NOT NULL DEFAULT '',
  `level2` decimal(10,2) NOT NULL DEFAULT '1.00' COMMENT '会员等级2折扣',
  `name2` varchar(40) NOT NULL DEFAULT '',
  `level3` decimal(10,2) NOT NULL DEFAULT '1.00' COMMENT '会员等级3折扣',
  `name3` varchar(40) NOT NULL DEFAULT '',
  `level4` decimal(10,2) NOT NULL DEFAULT '1.00' COMMENT '会员等级4折扣',
  `name4` varchar(40) NOT NULL DEFAULT '',
  `level5` decimal(10,2) NOT NULL DEFAULT '1.00' COMMENT '会员等级5折扣',
  `name5` varchar(40) NOT NULL DEFAULT '',
  `level6` decimal(10,2) NOT NULL DEFAULT '1.00' COMMENT '会员等级6折扣',
  `name6` varchar(40) NOT NULL DEFAULT '',
  PRIMARY KEY (`smlc_id`),
  UNIQUE KEY `U_SITE` (`site_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ev_site_transport`;
CREATE TABLE `ev_site_transport` (
  `trans_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '运费模板ID',
  `mall_id` smallint(4) NOT NULL DEFAULT '0' COMMENT '分站ID',
  `site_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '站点ID',
  `trans_title` varchar(255) NOT NULL DEFAULT '' COMMENT '运费模板名称',
  `is_default` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '是否网点的默认运费模板',
  `create_person` varchar(255) DEFAULT '' COMMENT '添加人',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`trans_id`),
  KEY `IX_SITE_ID` (`site_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='运费模板表';


DROP TABLE IF EXISTS `ev_site_transport_extend`;
CREATE TABLE `ev_site_transport_extend` (
  `trans_extend_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '运费模板扩展ID',
  `mall_id` smallint(4) NOT NULL DEFAULT '0' COMMENT '分站ID',
  `trans_id` int(10) unsigned NOT NULL COMMENT '运费模板ID',
  `trans_title` varchar(255) NOT NULL COMMENT '运费模板名称',
  `area_id` text NOT NULL COMMENT '市级地区ID组成的串，以，隔开，两端也有，',
  `top_area_id` text NOT NULL COMMENT '省级地区ID组成的串，以，隔开，两端也有，',
  `area_name` text NOT NULL COMMENT '地区name组成的串，以，隔开',
  `snum` mediumint(8) unsigned NOT NULL DEFAULT '1' COMMENT '首件数量',
  `sprice` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '首件运费',
  `xnum` mediumint(8) unsigned NOT NULL DEFAULT '1' COMMENT '续件数量',
  `xprice` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '续件运费',
  `is_default` enum('1','2') NOT NULL DEFAULT '2' COMMENT '是否默认运费1是2否',
  `create_person` varchar(255) DEFAULT '' COMMENT '添加人',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`trans_extend_id`),
  KEY `IX_TRANS_ID` (`trans_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='运费模板扩展表';


DROP TABLE IF EXISTS `ev_sms`;
CREATE TABLE `ev_sms` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `mobile` varchar(15) NOT NULL COMMENT '手机号码',
  `code` varchar(60) NOT NULL,
  `params` varchar(5000) NOT NULL COMMENT '参数',
  `content` varchar(200) NOT NULL COMMENT '内容',
  `ctime` bigint(12) unsigned NOT NULL COMMENT '创建时间',
  `ip` varchar(50) NOT NULL COMMENT 'ip',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '1未使用，2已使用',
  PRIMARY KEY (`id`),
  KEY `mobile` (`mobile`),
  KEY `status` (`status`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


DROP TABLE IF EXISTS `ev_store*`;
CREATE TABLE `ev_store*` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `seller_id` int(10) unsigned DEFAULT NULL COMMENT '商户id',
  `store_name` varchar(125) DEFAULT NULL COMMENT '门店名称',
  `mobile` varchar(13) DEFAULT NULL COMMENT '门店电话/手机号',
  `linkman` varchar(32) DEFAULT NULL COMMENT '门店联系人',
  `logo` char(32) DEFAULT NULL COMMENT '门店logo',
  `area_id` int(10) unsigned DEFAULT NULL COMMENT '门店地区id',
  `address` varchar(200) DEFAULT NULL COMMENT '门店详细地址',
  `coordinate` varchar(50) DEFAULT NULL,
  `ctime` bigint(12) unsigned DEFAULT NULL COMMENT '创建时间',
  `utime` bigint(12) unsigned DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


DROP TABLE IF EXISTS `ev_template`;
CREATE TABLE `ev_template` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `name` varchar(200) DEFAULT NULL COMMENT '模板名称',
  `th_template_id` int(10) unsigned DEFAULT NULL COMMENT '模板ID，第三方平台id',
  `create_time` int(10) unsigned DEFAULT '0' COMMENT '创建时间',
  `user_desc` varchar(200) DEFAULT NULL COMMENT '模板描述',
  `source_appid` varchar(100) DEFAULT NULL COMMENT '模板APPID',
  `developer` varchar(100) DEFAULT NULL COMMENT '模板开发者',
  `version` varchar(100) DEFAULT NULL COMMENT '模板版本，总共3位，第一位表示重大版本升级，第二位表示小版本升级，第三位表示补丁或更新',
  `image_id` char(32) DEFAULT NULL COMMENT '模板主图',
  `desc` text COMMENT '模板需要注意事项',
  `qr_demo` char(32) DEFAULT NULL COMMENT '二维码预览图片',
  `type` tinyint(1) unsigned DEFAULT '1' COMMENT '模板类型，1为小程序',
  `ext_json` text COMMENT '模板增加模板配置字段',
  `ctime` int(10) DEFAULT NULL COMMENT '数据创建时间',
  `utime` int(10) DEFAULT NULL COMMENT '数据更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='模板列表';


DROP TABLE IF EXISTS `ev_template_message`;
CREATE TABLE `ev_template_message` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '消息ID',
  `type` varchar(32) DEFAULT NULL COMMENT '消息类型',
  `code` varchar(32) DEFAULT NULL COMMENT '单号',
  `form_id` varchar(64) DEFAULT NULL COMMENT '要发生给的用户',
  `ctime` bigint(12) unsigned DEFAULT NULL COMMENT '创建时间',
  `utime` bigint(12) unsigned DEFAULT NULL COMMENT '更新时间',
  `status` tinyint(1) unsigned DEFAULT '1' COMMENT '发送状态 1=未发送 2=已发送',
  `isdel` bigint(12) unsigned DEFAULT NULL COMMENT '删除标识',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


DROP TABLE IF EXISTS `ev_template_order`;
CREATE TABLE `ev_template_order` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `template_id` int(10) unsigned DEFAULT '0' COMMENT '模板id',
  `appid` varchar(50) DEFAULT NULL COMMENT '商户appid',
  `ctime` bigint(12) unsigned DEFAULT NULL COMMENT '创建时间',
  `utime` bigint(12) unsigned DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='模板订购记录表';


DROP TABLE IF EXISTS `ev_user*`;
CREATE TABLE `ev_user*` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '用户ID',
  `username` varchar(20) DEFAULT NULL COMMENT '用户名',
  `password` char(32) DEFAULT NULL COMMENT '密码 md5(md5()+创建时间)',
  `mobile` varchar(15) DEFAULT NULL COMMENT '手机号',
  `sex` tinyint(1) unsigned DEFAULT '3' COMMENT '1=男 2=女 3=未知',
  `birthday` date DEFAULT NULL COMMENT '生日',
  `avatar` varchar(255) DEFAULT NULL COMMENT '头像',
  `nickname` varchar(50) DEFAULT NULL COMMENT '昵称',
  `balance` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '余额',
  `point` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '积分',
  `ctime` bigint(12) unsigned DEFAULT NULL,
  `utime` bigint(12) unsigned DEFAULT NULL,
  `status` tinyint(1) unsigned DEFAULT '1' COMMENT '1 = 正常 2 = 停用',
  `pid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '推荐人',
  `isdel` bigint(12) unsigned DEFAULT NULL COMMENT '删除标志 有数据就是删除',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='用户表';


DROP TABLE IF EXISTS `ev_user_bankcards*`;
CREATE TABLE `ev_user_bankcards*` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID号',
  `user_id` int(10) unsigned DEFAULT NULL COMMENT '用户ID',
  `bank_name` varchar(60) DEFAULT NULL COMMENT '银行名称',
  `bank_code` varchar(12) DEFAULT NULL COMMENT '银行缩写',
  `bank_area_id` int(10) unsigned DEFAULT NULL COMMENT '账号地区ID',
  `account_bank` varchar(255) DEFAULT NULL COMMENT '开户行',
  `account_name` varchar(60) DEFAULT NULL COMMENT '账户名',
  `card_number` varchar(30) DEFAULT NULL COMMENT '卡号',
  `card_type` tinyint(1) DEFAULT '1' COMMENT '银行卡类型: 1=储蓄卡 2=信用卡',
  `is_default` tinyint(1) unsigned DEFAULT '2' COMMENT '默认卡 1=默认 2=不默认',
  `ctime` bigint(12) unsigned DEFAULT NULL COMMENT '创建时间',
  `utime` bigint(12) unsigned DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


DROP TABLE IF EXISTS `ev_user_log`;
CREATE TABLE `ev_user_log` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `user_id` int(11) unsigned NOT NULL COMMENT '用户id',
  `state` tinyint(1) DEFAULT NULL COMMENT '登录 1  退出2,3注册',
  `ctime` bigint(12) unsigned DEFAULT NULL COMMENT '时间',
  `params` varchar(200) DEFAULT '' COMMENT '参数',
  `ip` varchar(15) DEFAULT NULL COMMENT 'ip地址',
  PRIMARY KEY (`id`),
  KEY `IX_USER` (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


DROP TABLE IF EXISTS `ev_user_point_log*`;
CREATE TABLE `ev_user_point_log*` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `mall_id` smallint(4) unsigned NOT NULL DEFAULT '0',
  `user_id` int(10) unsigned NOT NULL COMMENT '用户ID',
  `type` tinyint(1) unsigned DEFAULT '1' COMMENT '类型 1=签到 2=购物返积分 3=购物使用积分',
  `num` int(10) DEFAULT '0' COMMENT '积分数量',
  `balance` bigint(20) unsigned DEFAULT NULL COMMENT '积分余额',
  `remarks` varchar(255) DEFAULT NULL COMMENT '备注',
  `ctime` bigint(12) unsigned DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`),
  KEY `IX_USER` (`user_id`),
  KEY `IX_MALL` (`mall_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='用户积分记录表';


DROP TABLE IF EXISTS `ev_user_ship*`;
CREATE TABLE `ev_user_ship*` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `mall_id` int(10) unsigned NOT NULL DEFAULT '0',
  `user_id` int(10) unsigned DEFAULT '0' COMMENT '用户id 关联user.id',
  `area_id` int(10) unsigned DEFAULT '0' COMMENT '收货地区ID',
  `address` varchar(200) DEFAULT NULL COMMENT '收货详细地址',
  `name` varchar(50) DEFAULT NULL COMMENT '收货人姓名',
  `mobile` char(15) DEFAULT NULL COMMENT '收货电话',
  `utime` bigint(12) NOT NULL COMMENT '更新时间',
  `is_def` tinyint(1) unsigned DEFAULT NULL COMMENT '是否默认 1=默认 2=不默认',
  PRIMARY KEY (`id`),
  KEY `IX_USER` (`user_id`),
  KEY `IX_AREA` (`area_id`),
  KEY `IX_MALL` (`mall_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


DROP TABLE IF EXISTS `ev_user_tocash*`;
CREATE TABLE `ev_user_tocash*` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID号',
  `user_id` int(10) unsigned DEFAULT NULL COMMENT '用户ID',
  `money` decimal(8,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '提现金额',
  `bank_name` varchar(60) DEFAULT NULL COMMENT '银行名称',
  `bank_area_id` int(10) unsigned DEFAULT NULL COMMENT '账号地区ID',
  `account_bank` varchar(255) DEFAULT NULL COMMENT '开户行',
  `account_name` varchar(60) DEFAULT NULL COMMENT '账户名',
  `card_number` varchar(30) DEFAULT NULL COMMENT '卡号',
  `type` smallint(1) unsigned NOT NULL DEFAULT '1' COMMENT '1默认，2提现成功，3提现失败',
  `ctime` bigint(12) unsigned DEFAULT NULL COMMENT '创建时间',
  `utime` bigint(12) unsigned DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;


DROP TABLE IF EXISTS `ev_user_token*`;
CREATE TABLE `ev_user_token*` (
  `token` char(32) NOT NULL,
  `user_id` int(10) unsigned NOT NULL,
  `platform` smallint(2) NOT NULL DEFAULT '1' COMMENT '平台类型，1就是默认，2就是微信小程序',
  `ctime` bigint(12) unsigned NOT NULL,
  PRIMARY KEY (`token`),
  KEY `IX_USER` (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='用户token';


DROP TABLE IF EXISTS `ev_user_wx*`;
CREATE TABLE `ev_user_wx*` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '用户ID',
  `user_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '关联用户表',
  `openid` varchar(50) NOT NULL,
  `session_key` varchar(50) NOT NULL,
  `unionid` varchar(50) NOT NULL,
  `avatar` varchar(255) DEFAULT NULL COMMENT '头像',
  `nickname` varchar(50) DEFAULT NULL COMMENT '昵称',
  `gender` smallint(1) unsigned NOT NULL COMMENT '性别 0：未知、1：男、2：女',
  `language` varchar(50) NOT NULL COMMENT '语言',
  `city` varchar(80) NOT NULL COMMENT '城市',
  `province` varchar(80) NOT NULL COMMENT '省',
  `country` varchar(80) NOT NULL COMMENT '国家',
  `country_code` varchar(20) NOT NULL COMMENT '手机号码国家编码',
  `mobile` varchar(20) NOT NULL COMMENT '手机号码',
  `ctime` bigint(12) unsigned DEFAULT NULL,
  `utime` bigint(12) unsigned DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `IX_USER` (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='用户表';


DROP TABLE IF EXISTS `ev_user_wx_login*`;
CREATE TABLE `ev_user_wx_login*` (
  `wx_login_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `buyer_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '会员ID',
  `login_openid` varchar(50) NOT NULL DEFAULT '' COMMENT '用户openid',
  `login_appid` varchar(30) NOT NULL DEFAULT '' COMMENT '登录公众号',
  `login_nickname` varchar(50) NOT NULL DEFAULT '' COMMENT '登录的昵称',
  `login_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '用户登录时间',
  `is_subscribe` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '是否订阅',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`wx_login_id`),
  UNIQUE KEY `UK_BUYERWXLOGIN_openId` (`login_openid`),
  KEY `IX_BUYER_ID` (`buyer_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='买家登录记录表';


DROP TABLE IF EXISTS `ev_weixin_author`;
CREATE TABLE `ev_weixin_author` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `nick_name` varchar(255) DEFAULT NULL COMMENT '授权方昵称',
  `head_img` varchar(255) DEFAULT NULL COMMENT '授权方头像',
  `service_type_info` varchar(10) DEFAULT '0' COMMENT '默认为0',
  `verify_type_info` tinyint(1) DEFAULT '-1' COMMENT '授权方认证类型，-1代表未认证，0代表微信认证',
  `user_name` varchar(200) DEFAULT NULL COMMENT '小程序的原始ID',
  `signature` tinytext COMMENT '帐号介绍',
  `principal_name` varchar(255) DEFAULT NULL COMMENT '小程序的主体名称',
  `business_info` text COMMENT '用以了解以下功能的开通状况（0代表未开通，1代表已开通）： open_store:是否开通微信门店功能 open_scan:是否开通微信扫商品功能 open_pay:是否开通微信支付功能 open_card:是否开通微信卡券功能 open_shake:是否开通微信摇一摇功能',
  `qrcode_url` varchar(255) DEFAULT NULL COMMENT '二维码图片的URL',
  `authorization_info` text COMMENT '授权信息',
  `appid` varchar(255) DEFAULT NULL COMMENT '授权方appid',
  `appsecret` varchar(100) DEFAULT NULL COMMENT '授权方AppSecret',
  `miniprograminfo` tinytext COMMENT '可根据这个字段判断是否为小程序类型授权,有值为小程序',
  `func_info` text COMMENT '小程序授权给开发者的权限集列表，ID为17到19时分别代表： 17.帐号管理权限 18.开发管理权限 19.客服消息管理权限 请注意： 1）该字段的返回不会考虑小程序是否具备该权限集的权限（因为可能部分具备）',
  `authorizer_refresh_token` varchar(200) DEFAULT NULL COMMENT '刷新token',
  `authorizer_access_token` varchar(200) DEFAULT NULL COMMENT 'token',
  `bind_type` tinyint(1) unsigned DEFAULT '2' COMMENT '绑定类型，1为第三方授权绑定，2为自助绑定',
  `author_type` varchar(10) DEFAULT 'b2c' COMMENT '授权类型，默认b2c',
  `expires_in` int(10) unsigned DEFAULT '0' COMMENT '绑定授权到期时间',
  `ctime` int(10) unsigned DEFAULT '0' COMMENT '小程序授权时间',
  `utime` int(10) unsigned DEFAULT '0' COMMENT '小程序更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='获取授权方的帐号基本信息表';


DROP TABLE IF EXISTS `ev_weixin_publish`;
CREATE TABLE `ev_weixin_publish` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `template_id` int(10) unsigned DEFAULT '0' COMMENT '模板id，不是第三方平台模板id',
  `ctime` int(10) unsigned DEFAULT '0' COMMENT '购买模板时间',
  `audit_status` tinyint(1) DEFAULT NULL COMMENT '审核状态，其中0为审核成功，1为审核失败，2为审核中,-1为未提交审核',
  `reason` text COMMENT '审核反馈',
  `publish_status` tinyint(1) DEFAULT '0' COMMENT '发布状态，0未发布，1已发布，2发布成功，3发布失败',
  `publish_msg` text COMMENT '发布反馈',
  `auditid` varchar(50) DEFAULT NULL COMMENT '审核id',
  `ext_json` text COMMENT '第三方自定义的json',
  `user_version` varchar(50) DEFAULT '' COMMENT '代码版本号',
  `user_desc` varchar(200) DEFAULT NULL COMMENT '代码描述',
  `appid` varchar(100) DEFAULT NULL COMMENT '授权appid',
  `qrcode` varchar(255) DEFAULT NULL COMMENT '预览二维码',
  PRIMARY KEY (`id`)
